class InvalidRecipientPolicy(Exception):
    pass


class ImproperlyConfigured(Exception):
    pass


class MailLoopDetected(Exception):
    pass


class BaseFiltersException(Exception):
    """ Base class for filter exceptions """

    def __init__(self, reason, category):
        super(BaseFiltersException, self).__init__()
        self.category = category
        self.reason = reason
