from aes.message import MessageInfo
from aes.core.controller import BaseService


class NotSupported(Exception):
    """ Method not supported by this delivery type """


class DeliveryService(BaseService):
    async def process(self, msg: MessageInfo):
        """
        Send message to next hop (recipient, quarantine, static route, save
        to folder, etc.)
        """
        msg.log_delivery(self.name, {'target': 'null'})
