from aes.core.statistics import prometheus
from aes.engines.clam_av.engine import ClamAVEngine
from .base import AntivirusFilter


class ClamAVFilter(AntivirusFilter):
    engine_class = ClamAVEngine

    @prometheus('aes_filters_clamav')
    async def process(self, message, antivirus=None):
        return await super(ClamAVFilter, self).process(message, antivirus)
