from aes.core.statistics import prometheus
from aes.engines import CyrenAVEngine
from .base import AntivirusFilter


class CyrenAVFilter(AntivirusFilter):
    engine_class = CyrenAVEngine

    @prometheus('aes_filters_cyren_av')
    async def process(self, message, antivirus=None):
        return await super(CyrenAVFilter, self).process(message, antivirus)
