from aes.core.statistics import prometheus
from aes.engines import SophosEngine
from .base import AntivirusFilter


class SophosFilter(AntivirusFilter):
    engine_class = SophosEngine

    @prometheus('aes_filters_sophos')
    async def process(self, message, antivirus=None):
        return await super(SophosFilter, self).process(message, antivirus)
