from aes.core.exceptions import BaseFiltersException


class DenyException(BaseFiltersException):
    """
    Exception raised by filter to stop processing mail message and
    just deny it
    """


class ReturnToQueueException(Exception):
    """
    Exception raised by filter to stop processing mail message and
    return it to queue
    """


class BaseQuarantineException(BaseFiltersException):
    """ Base exception for quarantine message """

    def __init__(self, reason, category, smtp_endpoint):
        super(BaseQuarantineException, self).__init__(reason, category)
        self.smtp_endpoint = smtp_endpoint


class AdminQuarantineException(BaseQuarantineException):
    pass


class UserQuarantineException(BaseQuarantineException):
    pass
