from aes.core.statistics import prometheus
from aes.filters.antivirus.base import AntivirusFilter
from aes.engines import CyrenEngine


class CyrenFilter(AntivirusFilter):
    engine_class = CyrenEngine

    @prometheus('aes_filters_cyren')
    async def process(self, message, antivirus=None):
        return await super(CyrenFilter, self).process(message, antivirus)
