class SkipFlags:

    NOTHING = 0
    SPAM = 1 << 0
    ANTIVIRUS = 1 << 1
    ATTACHMENT = 1 << 2
    ATTACHMENTS = 1 << 2
    PHISHING = 1 << 3
    SPF = 1 << 4
    TLS_CHECK = 1 << 5
    CONTENT = 1 << 6
    URL_PROTECTION = 1 << 7
    MARKETING = 1 << 8

    def __getitem__(self, item):
        item = item.upper()
        if hasattr(self, item):
            return getattr(self, item)
        return self.NOTHING
