from unittest.mock import patch
import os
import importlib

import pytest

from aes.conf import Settings


def test_enviroment_variable_missed():
    with patch.object(os, 'environ', {}):
        conf = Settings()
        with pytest.raises(ValueError):
            conf.setup_with_env_variable(silent=False)


def test_node2_settings_not_fail():
    importlib.import_module('aes.conf.node2_settings')


def test_tribe_settings_not_fail():
    importlib.import_module('aes.conf.tribe_settings')
